PRAGMA encoding = 'UTF-8';
DROP TABLE IF EXISTS `utilisateur`;
CREATE TABLE IF NOT EXISTS `utilisateur` (
    `utilisateur_id`    INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT UNIQUE,
    `utilisateur_nom`   TINYTEXT NOT NULL,
    `utilisateur_prenom`   TINYTEXT NOT NULL,
    `utilisateur_login` VARCHAR(45) NOT NULL,
    `utilisateur_email` VARCHAR(45) NOT NULL,
    `utilisateur_mdp`    VARCHAR(100) NOT NULL);
DROP TABLE IF EXISTS `Plat`;
CREATE TABLE IF NOT EXISTS `Plat` (
   plat_id    INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT UNIQUE,
   plat_nom   TINYTEXT NOT NULL,
   plat_recette   VARCHAR(100) NOT NULL,
   plat_type  TINYTEXT NOT NULL,
   plat_nombre_convives INTEGER NOT NULL);
DROP TABLE IF EXISTS `ingredient`;
CREATE TABLE IF NOT EXISTS `ingredient` (
   `ingredient_id`    INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT UNIQUE,
   `ingredient_nom`   TINYTEXT NOT NULL,
   `ingredient_type`  TINYTEXT NOT NULL);
DROP TABLE IF EXISTS composition;
CREATE TABLE IF NOT EXISTS composition (
   recette_id    INTEGER NOT NULL REFERENCES plat(plat_id),
   ingr_id  INTEGER NOT NULL ingredient(ingredient_id),
   composition_quantite   INTEGER NOT NULL);
COMMIT;

